CREATE TABLE trabajador (seguro_social integer, nombre varchar2(80), paterno varchar2(80),constraint pk_trabajador primary key(seguro_social));--creamos la secuancia para la nominacreate sequence sec_nominastart with 1increment by 1nomaxvalue;--creamos la segunda tablacreate table nomina (id_nomina integer, seguro_social integer, horas_laboradas integer, fecha_pago date, saldo float,constraint pk_id_nomina primary key (id_nomina),constraint fk1_seguro_social foreign key (seguro_social)references trabajador(seguro_social));--procedimento para guardar trabajadorcreate or replace procedure guardar_trabajador(my_id in integer, my_nombre in varchar2, my_paterno in varchar2) asbegininsert into trabajador values (my_id,my_nombre,my_paterno);end;/create or replace procedure guardar_nomina (my_id_nomina out integer, my_seguro_social in integer) asbeginselect sec_nomina.nextval into my_id_nomina from dual;insert into nomina (id_nomina, seguro_social) values (my_id_nomina,my_seguro_social);end;/declare VALOR INTEGER; BEGIN guardar_trabajador(333,'juan','campos'); guardar_nomina(valor,333);end; /    select * from trabajador
